installer-script.script:
function installCheck() {
    if(system.files.fileExistsAtPath('/usr/local/bin/kd')) {
	    my.result.title = 'Previous Installation Detected';
	    my.result.message = 'A previous installation of Koding Kite Tool exists at  /usr/local/bin. This installer will remove the previous installation prior to installing. Please back up any data before proceeding.';
	    my.result.type = 'Warning';
	    return false;
	}
    return true;
}
